namespace vm2.RegexLib.Tests;

public class WindowsPathnameTests(
    RegexLibTestsFixture fixture,
    ITestOutputHelper output) : RegexTests(fixture, output)
{
    static readonly string boarderLengthName = new('a', 260);
    static readonly string overBoardLengthName = new('a', 261);

    public static TheoryData<string, bool, string, Captures?> WinFilenameData => new() {
        { TestFileLine(), false, "", null },
        { TestFileLine(), false, " ", null },
        { TestFileLine(), false, ".", null },
        { TestFileLine(), false, "..", null },
        { TestFileLine("File with device name in it should not match"), false, "con", null },
        { TestFileLine("File with device name in it should not match"), false, "con.txt", null },
        { TestFileLine("File with device name in it should not match"), false, "lpt1", null },
        { TestFileLine("File with device name in it should not match"), false, "lpt1.txt", null },
        { TestFileLine(), true,  "a", new() {["file"] = "a", ["name"] = "a"} },
        { TestFileLine(), true,  "C", new() {["file"] = "C", ["name"] = "C"} },
        { TestFileLine(), true,  "ж", new() {["file"] = "ж", ["name"] = "ж"} },
        { TestFileLine(), true,  "Ж", new() {["file"] = "Ж", ["name"] = "Ж"} },
        { TestFileLine(), true,  "ab", new() {["file"] = "ab", ["name"] = "ab"} },
        { TestFileLine(), true,  "QWERTYUIOPASDFGHJKLZXCVBNMqwertyuiopasdfghjklzxcvbnm", new() {["file"] = "QWERTYUIOPASDFGHJKLZXCVBNMqwertyuiopasdfghjklzxcvbnm", ["name"] = "QWERTYUIOPASDFGHJKLZXCVBNMqwertyuiopasdfghjklzxcvbnm"} },
        { TestFileLine(), true,  "QWERTYUIOPASDFGHJKLZXCVBNM qwertyuiopasdfghjklzxcvbnm", new() {["file"] = "QWERTYUIOPASDFGHJKLZXCVBNM qwertyuiopasdfghjklzxcvbnm", ["name"] = "QWERTYUIOPASDFGHJKLZXCVBNM qwertyuiopasdfghjklzxcvbnm"} },
        { TestFileLine(), true,  "QWERTYUIOPASDFGHJKLZXCVBNM.qwertyuiopasdfghjklzxcvbnm", new() {["file"] = "QWERTYUIOPASDFGHJKLZXCVBNM.qwertyuiopasdfghjklzxcvbnm", ["suffix"] = "qwertyuiopasdfghjklzxcvbnm", ["name"] = "QWERTYUIOPASDFGHJKLZXCVBNM"} },
        { TestFileLine("File name ending on '.' should not match"), false, "QWERTYUIOPASDFGHJKLZXCVBNMqwertyuiopasdfghjklzxcvbnm.", null },
        { TestFileLine("File name ending on ' ' should not match"), false, "QWERTYUIOPASDFGHJKLZXCVBNMqwertyuiopasdfghjklzxcvbnm ", null },
        { TestFileLine(), true,  boarderLengthName, new()
            {
                ["file"] = "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa",
                ["name"] = "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa",
            } },
        { TestFileLine("File name longer than 260 characters should not match"), false, overBoardLengthName, null },
        { TestFileLine(), true,  "явертъуиопасдфгхйклзьцжбнмшщюч ЯВЕРТЪУИОПАСДФГХЙКЛЗЬЦЖБНМШЩЮЧ", new() {["file"] = "явертъуиопасдфгхйклзьцжбнмшщюч ЯВЕРТЪУИОПАСДФГХЙКЛЗЬЦЖБНМШЩЮЧ",["name"] = "явертъуиопасдфгхйклзьцжбнмшщюч ЯВЕРТЪУИОПАСДФГХЙКЛЗЬЦЖБНМШЩЮЧ",} },
        // Edge and additional tests
        { TestFileLine("File name with dash and underscore"), true, "foo-bar_baz", new() {["file"] = "foo-bar_baz", ["name"] = "foo-bar_baz"} },
        { TestFileLine("File name with space"), true, "file name.txt", new() {["file"] = "file name.txt", ["name"] = "file name", ["suffix"] = "txt",} },
        { TestFileLine("File name with Unicode"), true, "файл.txt", new() {["file"] = "файл.txt", ["name"] = "файл", ["suffix"] = "txt",} },
        { TestFileLine("File name with leading dot"), true, ".hidden", new() {["file"] = ".hidden", ["name"] = ".hidden"} },
        { TestFileLine("File name with multiple dots"), true, "archive.tar.gz", new() {["file"] = "archive.tar.gz", ["name"] = "archive.tar", ["suffix"] = "gz", } },
        { TestFileLine("File name with trailing dot (should not match)"), false, "file.", null },
        { TestFileLine("File name with trailing space (should not match)"), false, "file ", null },
        { TestFileLine("File name with reserved character < (should not match)"), false, "file<name", null },
        { TestFileLine("File name with reserved character > (should not match)"), false, "file>name", null },
        { TestFileLine("File name with reserved character : (should not match)"), false, "file:name", null },
        { TestFileLine("File name with reserved character \" (should not match)"), false, "file\"name", null },
        { TestFileLine("File name with reserved character / (should not match)"), false, "file/name", null },
        { TestFileLine("File name with reserved character \\ (should not match)"), false, "file\\name", null },
        { TestFileLine("File name with reserved character | (should not match)"), false, "file|name", null },
        { TestFileLine("File name with reserved character ? (should not match)"), false, "file?name", null },
        { TestFileLine("File name with reserved character * (should not match)"), false, "file*name", null },
        { TestFileLine("File name with null character (should not match)"), false, "abc\0def", null },
        { TestFileLine("File name with only slashes (should not match)"), false, "////", null },
        { TestFileLine("File name with only spaces (should not match)"), false, "   ", null },
        { TestFileLine("File name with only underscores"), true, "____", new() {["file"] = "____",["name"] = "____",} },
        { TestFileLine("File name with only dashes"), true, "----", new() {["file"] = "----",["name"] = "----",} },
        { TestFileLine("File name with only numbers"), true, "1234567890", new() {["file"] = "1234567890",["name"] = "1234567890",} },
        { TestFileLine("File name with max length 260"), true, boarderLengthName, new()
            {
                ["file"] = "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa",
                ["name"] = "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa",
            } },
        { TestFileLine("File name with length 261 (should not match)"), false, overBoardLengthName, null },
    };

    [Theory]
    [MemberData(nameof(WinFilenameData))]
    public void TestWinFilename(string TestLine, bool shouldBe, string fileName, Captures? captures)
        => base.RegexTest(WindowsPathname.DiskFilename(), TestLine, shouldBe, fileName, captures);

