        { TestFileLine(), false, "", null },
        { TestFileLine(), false, " ", null },
        { TestFileLine(), false, ".", null },
        { TestFileLine(), false, "..", null },
        { TestFileLine(), false, "con", null },
        { TestFileLine(), false, "con.txt", null },
        { TestFileLine(), false, "lpt1", null },
        { TestFileLine(), false, "lpt1.txt", null },
        { TestFileLine(), false, "a.", null },
        { TestFileLine(), false, "a ", null },
