        { TestFileLine(), false, @"a:com1", null },
        { TestFileLine(), false, @"ъ:a", null },
        { TestFileLine(), false, @"a:"+boarderLengthName, new() { ["drive"] = "a", ["path"] = "", ["file"] = boarderLengthName, ["name"] = boarderLengthName } },
        { TestFileLine(), false, @"a:a.", null },
        { TestFileLine(), false, @"a:a ", null },
        { TestFileLine(), true, @"a:a", new() { ["drive"] = "a", ["path"] = "", ["file"] = "a", ["name"] = "a" } },
        { TestFileLine(), true, @"B:a", new() { ["drive"] = "B", ["path"] = "", ["file"] = "a", ["name"] = "a" } },
        { TestFileLine(), false, @"a:/com2", null },
        { TestFileLine(), false, @"a:/com2.bin", null },
        { TestFileLine(), true, @"a:", new() { ["drive"] = "a", } },
