        { TestFileLine(), false, @"a:/b/c /d/lp.txt", null },
        { TestFileLine(), true, @"a:\", new() { ["drive"] = "a", ["path"] = @"\" } },
        { TestFileLine(), true, @"a:/", new() { ["drive"] = "a", ["path"] = @"/" } },
