    [Theory]
    [MemberData(nameof(WinPathnameData))]
    public void TestWinPathname(string TestLine, bool shouldBe, string pathname, Captures? captures)
        => base.RegexTest(WindowsPathname.Pathname(), TestLine, shouldBe, pathname, captures);
